// @flow
import request from "./request";
import type { CommissionGroup } from "../types/Commission";

/**
 * @module
 * @example import { Commission } from "drivewealth-back-office-javascript";
 */
export default class Commission {
	/**
	 * List all available commission groups
	 */
	static getAllCommissionGroups(): Promise<Array<CommissionGroup>> {
		return request({
			endpoint: "/commissions",
		}).then(data => data.body.commissionGroups);
	}
}
