// @flow
/**
 * @memberof Types
 */
export type Rate = {
	fractional: {
		rate: number,
		passThroughFees: boolean,
		shareAmountRounding: string,
		minimumRate: number,
	},
	basket: {
		overageRate: number,
		baseNumberOfShares: number,
		passThroughFees: boolean,
		schedule: { [string]: number },
		default: {
			rate: number,
			passThroughFees: boolean,
			shareAmountRounding: string,
			minimumRate: number,
		},
	},
};

/**
 * @memberof Types
 */
export type Commission = {
	active: boolean,
	commissionID: string,
	description: string,
	assignmentCriteria: {
		wlpID: string,
		country: string,
		defaultStatus: boolean,
		accountMgmtType: string | null
	},
	rates: Array<Rate>,
	count: number,
	type: string,
};

/**
 * @memberof Types
 */
export type CommissionGroup = {
	ib: {
		id: string,
		name: string,
	},
	commissions: Array<Commission>,
	count: number,
	type: string,
};
